const botPrefix = tempVars('botPrefix');
var reactionArray = [];
var rectarray = globalVars('reactionRoleInf');
const _this = this;
var tempRole;
var emojiArray = [];
var roleArray = [];

msg.channel.send(`:sneezing_face: _**TAG POR REAÇÕES**_:smiling_imp: \n_Bem vindo(a)!_\n\n-Por favor, mencione agora a TAG do Evento, mais um emoji.\n**Ex. @TAGmensionável emoji**✅`)
    .then((message) => {
        msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
                max: 1,
                time: 60000,
                errors: ['time'],
            })
            .then(async function(collected) {
                msg.author.lastMessage.delete();
                if (collected.first().content.split(/ +/g).length > 1) {
                    if (collected.first().content.split(/ +/g)[0] != null) {
                        const tstValidRole = await msg.guild.roles.find(role => role.name === collected.first().content.split(/ +/g)[0]);
                        if (collected.first().mentions.roles.first() || tstValidRole != null) {
                            var unicodeStored = collected.first().content.split(/ +/g)[1];
                            var roleMentioned;
                            if (collected.first().mentions.roles.first() != null) {
                                roleMentioned = collected.first().mentions.roles.first();
                            } else {
                                roleMentioned = tstValidRole;
                            }
                            if (unicodeStored.charCodeAt(0 > 127)) {
                                reactionArray.push([unicodeStored, roleMentioned.id, roleMentioned.name]);
                                emojiArray.push(unicodeStored);
                                roleArray.push(roleMentioned.id);
                                message.edit(`-Você anexou a TAG=> **${roleMentioned.name}** ao Emoji=> ${unicodeStored}.\n-Se estiver correto, responda "sim".\n-Caso contrário, responda "cancelar" para cancelar a ação.`);
                                msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
                                        max: 1,
                                        time: 60000,
                                        errors: ['time'],
                                    })
                                    .then((collected) => {
                                        msg.author.lastMessage.delete();
                                        if (collected.first().content.toLowerCase() == "sim") {
                                            reAsk(message)
                                        } else if (collected.first().content.toLowerCase() == "cancelar") {
                                            message.delete();
                                            msg.channel.send(":warn: O aviso foi cancelado.\n-Por favor, execute novamente o comando para anexar a TAG a reação!");
                                        } else {
                                            message.delete();
                                            msg.channel.send(":warn: O prompt foi cancelado, você não forneceu uma resposta válida!\n-Por favor, execute novamente o comando para anexar a TAG a reação!");
                                        }
                                    }).catch(() => {
                                        message.delete();
                                        msg.channel.send(':hourglass: Desculpe, você não forneceu uma resposta válida a tempo!\n-Por favor, execute novamente o comando para anexar a TAG a reação!');
                                    });
                            } else {
                                message.delete();
                                msg.channel.send(":warn: O aviso foi **Cancelado**, você não forneceu um emoji válido!\nPor favor, execute novamente o comando para anexar a TAG a reação");
                            }

                        } else {
                            message.delete();
                            msg.channel.send(":warn: O aviso foi cancelado, você não forneceu uma resposta válida!\nPor favor, execute novamente o comando para anexar a TAG a reação!");
                        }
                    } else {
                        message.delete();
                        msg.channel.send(":warn: O aviso foi cancelado, você não forneceu uma TAG mensionável válida!\nPor favor, execute novamente o comando para anexar a TAG a reação!");
                    }
                } else {
                    message.delete();
                    msg.channel.send(":warn: O prompt foi cancelado, você não forneceu uma TAG/emoji mensionável!\nPor favor, execute novamente o comando para anexar a TAG a reação!");
                }
            }).catch((error) => {
                console.log(error);
                message.delete();
                msg.channel.send(':hourglass: Desculpe, você não forneceu uma resposta válida a tempo!\nPor favor, execute novamente o comando para anexar a TAG a reação!');
            });
    })

function reAsk(message) {
    if (reactionArray.length != 10) {
        var finalText = "";
        reactionArray.forEach(subarray => {
            finalText = finalText + `${subarray[0]} => ${subarray[2]}\n`
        })
        message.edit(`:smirk: Você pode anexar até 10 TAGs em 10 Reações!\n ${finalText}\n\n-Se você gostaria de anexar outra reação a uma TAG, insira uma TAG junto com um outro emoji.\nSe não, por favor responda com "pronto".\nSe você deseja cancelar o aviso, responda com "cancelar"`);
        msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
                max: 1,
                time: 60000,
                errors: ['time'],
            })
            .then(async function(collected) {
                msg.author.lastMessage.delete();
                if (collected.first().content.toLowerCase() == "pronto") {
                    done(message)
                } else if (collected.first().mentions.roles.first() == "cancelar") {
                    message.delete();
                    msg.channel.send(":warn: O aviso foi cancelado.\n-Por favor, execute novamente o comando para anexar a TAG a reação!");
                } else {
                    if (collected.first().content.split(/ +/g).length > 1) {
                        const tstValidRole = await msg.guild.roles.find(role => role.name === collected.first().content.split(/ +/g)[0]);
                        if (collected.first().mentions.roles.first() || tstValidRole != null) {
                            var unicodeStored = collected.first().content.split(/ +/g)[1];
                            if (collected.first().mentions.roles.first() != null) {
                                roleMentioned = collected.first().mentions.roles.first();
                            } else {
                                roleMentioned = tstValidRole;
                            }
                            if (unicodeStored != null) {
                                if (!emojiArray.includes(unicodeStored)) {
                                    if (!roleArray.includes(roleMentioned.id)) {
                                        if (unicodeStored.charCodeAt(0 > 127)) {
                                            reactionArray.push([unicodeStored, roleMentioned.id, roleMentioned.name]);
                                            emojiArray.push(unicodeStored);
                                            roleArray.push(roleMentioned.id);
                                            reAsk(message)
                                        } else {
                                            message.delete();
                                            msg.channel.send("-:warn: O prompt foi cancelado, você usou o mesmo emoji duas vezes para funções diferentes!\nPor favor, execute novamente o comando para anexar a TAG a reação!");
                                        }
                                    } else {
                                        message.delete();
                                        msg.channel.send("-:warn: O prompt foi cancelado, você usou a mesma TAG duas vezes no prompt!\nPor favor, execute novamente o comando para anexar a TAG a reação!");
                                    }
                                } else {
                                    message.delete();
                                    msg.channel.send(":warn: O aviso foi cancelado, você não forneceu um emoji válido!\nPor favor,execute novamente o comando para anexar a TAG a reação!");
                                }
                            }
                        } else {
                            message.delete();
                            msg.channel.send(":warn: O prompt foi cancelado, você não mencionou um papel válido!\nPor favor,execute novamente o comando para anexar a TAG a reação!");
                        }
                    } else {
                        message.delete();
                        msg.channel.send(":warn: Prompt foi cancelado, você não forneceu uma TAG emoji/mencionado!\nPor favor,execute novamente o comando para anexar a TAG a reação!");
                    }
                }
            }).catch(() => {
                message.delete();
                msg.channel.send('-:hourglass: Desculpe, você não forneceu uma resposta válida a tempo!\nPor favor,execute novamente o comando para anexar a TAG a reação!');
            });
    } else {
        done(message);
    }

}

function done(message) {
    var finalText = "";
    reactionArray.forEach(subarray => {
        finalText = finalText + `${subarray[0]} => ${subarray[2]}\n`
    })
    message.edit(`-Menu final de conformação\n\n-Abaixo está a reações a sua TAGs\n${finalText}\n-Se isso estiver correto, mencione um canal de texto no qual o painel com a reação será enviado.\nEx.: "#geral"\nSe isso não estiver correto, responda com "cancelar" para cancelar o aviso.`)
    msg.channel.awaitMessages(response => response.author.id == msg.author.id, {
            max: 1,
            time: 60000,
            errors: ['time'],
        })
        .then((collected) => {
            msg.author.lastMessage.delete();
            if (collected.first().mentions.channels.first()) {
                var menchannel = collected.first().mentions.channels.first()
                message.delete();
                menchannel.send({
                        embed: {
                            color: 0xffff00,
                            title: "_**TAGS por Reação**_",
                            description: `-Clique na reação abaixo para obter a TAG Ingresso do evento.\n${finalText}`,
                            timestamp: new Date()
                        }
                    })
                    .then(embedmessage => {
                        reactionArray.forEach(async function(subarray) {
                            await embedmessage.react(subarray[0])
                        })
                        rectarray.push({
                            [embedmessage.id]: reactionArray
                        })
                        _this.storeValue(rectarray, 3, "reactionRoleInf", cache);
                        _this.callNextAction(cache);
                    })
            } else if (collected.first().content == "cancelar") {
                message.delete();
                msg.channel.send("-:warn: O aviso foi cancelado.\nPor favor, execute novamente o comando para passar pelo prompt novamente!");
            } else {
                message.delete();
                msg.channel.send("-:warn: O aviso foi cancelado, você não forneceu uma resposta válida!\nPor favor, execute novamente o comando para passar pelo prompt novamente!");
            }
        }).catch(() => {
            message.delete();
            msg.channel.send('-:warn: Desculpe, você não forneceu uma resposta válida a tempo!\nPor favor, execute novamente o comando para passar pelo prompt novamente!');
        });
}

