if (typeof tempVars("emitType") == 'undefined') {
   var emitReq = "nothing";
} else {
   var emitReq = tempVars("emitType").toUpperCase();
};

switch(emitReq) {
	case "MEMBROENTRAR": 
		this.getDBM().Bot.bot.emit("guildMemberAdd", msg.member); 
		msg.reply("Certo, foi emitido o evento `guildMemberAdd`"); 
		break;
	case "MEMBROSAIR":
		this.getDBM().Bot.bot.emit("guildMemberRemove", msg.member); 
		msg.channel.send("Certo, foi emitido o evento `guildMemberRemove`"); 
		break;
	case "MEMBROBAN": 
		this.getDBM().Bot.bot.emit("guildBanAdd", msg.guild, msg.author); 
		msg.channel.send("Certo, foi emitido o evento `guildBanAdd`"); 
		break;
	case "UNBAN": 
		this.getDBM().Bot.bot.emit("guildBanRemove", msg.guild, msg.author); 
		msg.channel.send("Certo, foi emitido o evento `guildBanRemove`"); 
		break;
	case "ERRO": 
		msg.channel.send("Certo, foi emitido o evento `error` - verifique o console");
		this.getDBM().Bot.bot.emit("error", "[ADVANCEDBOT] Isto e um erro de teste"); 
		break;
	case "AODIGITAR": 
		msg.channel.send("Certo, foi emitido o evento `aoDigitar`");
		this.getDBM().Bot.bot.emit("typingStart", msg.author); 
		break;
	case "AOPARARDIGITAR": 
		msg.channel.send("Certo, foi emitido o evento `aoPararDigitar`");
		this.getDBM().Bot.bot.emit("typingStop", msg.author); 
		break;	
	case "DELBULKMSG": 
		msg.channel.send("Certo, foi emitido o evento `delBulkMsg`");
		this.getDBM().Bot.bot.emit("messageDeleteBulk", msg.author); 
		break;
	case "MSGREMREACTION": 
		msg.channel.send("Certo, foi emitido o evento `msgRemReaction`");
		this.getDBM().Bot.bot.emit("messageReactionRemove", msg.author); 
		break;
	case "MSGADDREACTION": 
		msg.channel.send("Certo, foi emitido o evento `msgAddReaction`");
		this.getDBM().Bot.bot.emit("messageReactionAdd", msg.author); 
		break;
	default: 
		msg.channel.send("A sintaxe para este comando é `!emit <tipo>` \n\n**Os tipos que você pode emitir são:** \n`membroEntrar` - simular a entrada de um membro \n`membroSair` - simular a saida de um membro \n`membroBan` - simular que um membro é banido \n`unban` - simular que um membro foi desbanido \n`error` - simlar um erro\n`aoDigitar`/`aoPararDigitar` - simular um membro digitando/parando de digitar\n`delBulkMsg` - simular o bot removendo X mensagens\n`msgAddReaction`/`msgRemReaction` - simular você removendo/adicionando reações a uma mensagem.");
		break;
};

